<script setup lang="ts">
export type Color = 'light' | 'dark' | 'colorful' | 'white'

const props = defineProps<{ color: Color }>()
const { color } = toRefs(props)

const getPathColor = (pathNumber: number): string => {
  if (color.value === 'light') {
    return '#3066BE'
  } else if (color.value === 'dark') {
    return '#0E1F4D'
  } else if (color.value === 'white') {
    return '#fff'
  } else {
    if (pathNumber === 1) return '#3066BE'
    if (pathNumber === 2) return '#0E1F4D'
    return `url(#gradient${pathNumber})`
  }
}
